mapper.update:
  	UPDATE AUCTIONS
	SET MAXPRICE = #{param1}
	WHERE AUCTIONID = #{param2}

UPDATE AUCTIONS
SET  COUNT=#{count}
WHERE AUCTIONID=#{auctionId}

UPDATE AUCTIONS
SET sendNoti=1
WHERE AUCTIONID=#{auctionId}

mapper.select:
 	SELECT DISTINCT b.userId
	FROM AUCTIONS a, BIDS b
	WHERE a.auctionId = b.auctionId AND a.maxPrice = b.bidPrice AND a.auctionId = #{auctionId}
